import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EmployeeList.css'; // Importing CSS file
import { ToastContainer, toast } from 'react-toastify';  // Importing react-toastify
import 'react-toastify/dist/ReactToastify.css';  // Importing styles for toast

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentEmployee, setCurrentEmployee] = useState({
    id: null,
    name: '',
    age: '',
    department: ''
  });

  // Fetch all employees from the backend
  useEffect(() => {
    fetchEmployees();
  }, []);

  // Fetch employees from backend
  const fetchEmployees = () => {
    axios.get('http://localhost:8000/employees/')
      .then(response => {
        setEmployees(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the employees!', error);
      });
  };

  // Delete an employee
  const handleDelete = (id) => {
    axios.delete(`http://localhost:8000/employees/${id}`)
      .then(() => {
        setEmployees(employees.filter(employee => employee.id !== id)); // Remove the deleted employee from the state
        toast.success('Employee deleted successfully!');
      })
      .catch(error => {
        console.error('There was an error deleting the employee!', error);
        toast.error('Failed to delete employee!');
      });
  };

  // Handle updating an employee
  const handleUpdate = (employee) => {
    setIsEditing(true);
    setCurrentEmployee({
      id: employee.id,
      name: employee.name,
      age: employee.age,
      department: employee.department
    });
  };

  // Submit the updated employee info
  const handleSubmitUpdate = (e) => {
    e.preventDefault();
    const { id, name, age, department } = currentEmployee;

    // Validate age first
    const ageNumber = parseInt(age, 10);
    if (isNaN(ageNumber) || ageNumber < 16) {
      toast.error('Employee must be at least 16 years old.');
      return;
    }

    // If age is valid, continue with the rest of the update
    axios.put(`http://localhost:8000/employees/${id}`, { name, age: ageNumber, department })
      .then(response => {
        setEmployees(employees.map(employee =>
          employee.id === id ? response.data : employee
        ));
        setIsEditing(false); // Close the edit form
        toast.success('Employee updated successfully!');
      })
      .catch(error => {
        console.error('There was an error updating the employee!', error);
        toast.error('Failed to update employee!');
      });
  };

  // Handle adding new employee
  const handleSubmitAdd = (e) => {
    e.preventDefault();
    const { name, age, department } = currentEmployee;

    // Validate age first
    const ageNumber = parseInt(age, 10);
    if (isNaN(ageNumber) || ageNumber < 16) {
      toast.error('Employee must be at least 16 years old.');
      return;
    }

    // If age is valid, proceed to add the new employee
    axios.post('http://localhost:8000/employees/', { name, age: ageNumber, department })
      .then(response => {
        setEmployees([...employees, response.data]);  // Add the new employee to the state
        setIsEditing(false); // Close the form
        toast.success('Employee added successfully!');
      })
      .catch(error => {
        console.error('There was an error adding the employee!', error);
        toast.error('Failed to add employee!');
      });
  };

  // Handle input changes in the form
  const handleChange = (e) => {
    const { name, value } = e.target;
    setCurrentEmployee(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  // Handle clearing the form fields
  const handleClearForm = () => {
    setCurrentEmployee({
      id: null,
      name: '',
      age: '',
      department: ''
    });
    setIsEditing(false); // Close the form
  };

  return React.createElement('div', { className: 'employee-list-container' },
    React.createElement('h2', { className: 'employee-list-heading' }, 'Employee List'),

    // Employee List
    React.createElement('div', { className: 'employee-cards-container' },
      employees.map(employee =>
        React.createElement('div', { key: employee.id, className: 'employee-card' },
          React.createElement('h3', null, employee.name),
          React.createElement('p', null, React.createElement('strong', null, 'ID: '), employee.id),
          React.createElement('p', null, React.createElement('strong', null, 'Age: '), employee.age),
          React.createElement('p', null, React.createElement('strong', null, 'Department: '), employee.department),
          React.createElement('div', { className: 'employee-card-actions' },
            React.createElement('button', { className: 'btn-delete', onClick: () => handleDelete(employee.id) }, 'Delete'),
            React.createElement('button', { className: 'btn-update', onClick: () => handleUpdate(employee) }, 'Update')
          )
        )
      )
    ),

    // Edit or Add Form Modal
    isEditing &&
    React.createElement('div', { className: 'modal' },
      React.createElement('div', { className: 'modal-content' },
        React.createElement('h3', null, currentEmployee.id ? 'Edit Employee' : 'Add Employee'),
        React.createElement('form', { onSubmit: currentEmployee.id ? handleSubmitUpdate : handleSubmitAdd },
          React.createElement('label', null,
            'Name:',
            React.createElement('input', {
              type: 'text',
              name: 'name',
              value: currentEmployee.name,
              onChange: handleChange,
              required: true
            })
          ),
          React.createElement('label', null,
            'Age:',
            React.createElement('input', {
              type: 'number',
              name: 'age',
              value: currentEmployee.age,
              onChange: handleChange,
              required: true
            })
          ),
          React.createElement('label', null,
            'Department:',
            React.createElement('input', {
              type: 'text',
              name: 'department',
              value: currentEmployee.department,
              onChange: handleChange,
              required: true
            })
          ),
          React.createElement('div', { className: 'modal-buttons' },
            React.createElement('button', { type: 'submit' }, 'Save Changes'),
            React.createElement('button', { type: 'button', onClick: handleClearForm }, 'Clear'),
            React.createElement('button', { type: 'button', onClick: () => setIsEditing(false) }, 'Cancel')
          )
        )
      )
    ),

    // Toast Notifications
    React.createElement(ToastContainer, { position: 'top-right', autoClose: 5000, hideProgressBar: false })
  );
};

export default EmployeeList;
