// src/App.js
import React, { useState } from "react";
import EmployeeForm from "./components/EmployeeForm";
import EmployeeList from "./components/EmployeeList";

const App = () => {
    const [selectedEmployeeId, setSelectedEmployeeId] = useState(null);

    const handleSave = (employee) => {
        setSelectedEmployeeId(null);  // Reset the selected employee after saving
    };

    const handleUpdate = (id) => {
        setSelectedEmployeeId(id);  // Set the employee ID to edit
    };

    return (
        <div>
            <h1>Employee Management</h1>
            <EmployeeForm employeeId={selectedEmployeeId} onSave={handleSave} />
            <EmployeeList onUpdate={handleUpdate} />
        </div>
    );
};

export default App;
