import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import {
  Trash2,
  Edit2,
  PlusCircle,
  User,
  Clock,
  Briefcase
} from 'lucide-react';
import './EmployeeList.css';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentEmployee, setCurrentEmployee] = useState({
    id: null,
    name: '',
    age: '',
    department: ''
  });

  // Fetch all employees from the backend
  useEffect(() => {
    fetchEmployees();
  }, []);

  // Fetch employees from backend
  const fetchEmployees = () => {
    axios.get('http://localhost:8000/employees/')
      .then(response => {
        // Sort employees alphabetically by name
        const sortedEmployees = response.data.sort((a, b) =>
          a.name.localeCompare(b.name)
        );
        setEmployees(sortedEmployees);
      })
      .catch(error => {
        console.error('There was an error fetching the employees!', error);
        toast.error('Failed to fetch employees!');
      });
  };

  // Delete an employee
  const handleDelete = (id) => {
    // Add confirmation before deletion
    if (window.confirm('Are you sure you want to delete this employee?')) {
      axios.delete(`http://localhost:8000/employees/${id}`)
        .then(() => {
          setEmployees(employees.filter(employee => employee.id !== id));
          toast.success('Employee deleted successfully!');
        })
        .catch(error => {
          console.error('There was an error deleting the employee!', error);
          toast.error('Failed to delete employee!');
        });
    }
  };

  // Handle updating an employee
  const handleUpdate = (employee) => {
    setIsEditing(true);
    setCurrentEmployee({
      id: employee.id,
      name: employee.name,
      age: employee.age,
      department: employee.department
    });
  };

  // Handle adding new employee
  const handleSubmitAdd = (e) => {
    e.preventDefault();
    const { name, age, department } = currentEmployee;

    // Convert age to number
    const ageNumber = parseInt(age, 10);

    // Client-side validation first
    if (isNaN(ageNumber) || ageNumber < 16) {
      toast.error('Employee must be at least 16 years old.');
      return;
    }

    axios.post('http://localhost:8000/employees/', {
      name,
      age: ageNumber,
      department
    })
      .then(response => {
        fetchEmployees();
        setIsEditing(false);
        toast.success('Employee added successfully!');
      })
      .catch(error => {
        console.error('There was an error adding the employee!', error);

        // Comprehensive error handling
        if (error.response) {
          if (error.response.status === 422) {
            const errorDetails = error.response.data;
            const errorMessage = errorDetails.detail[0].msg || 'Validation failed';
            toast.error(errorMessage);
          } else {
            const errorMessage = error.response.data.detail || 'Failed to add employee!';
            toast.error(errorMessage);
          }
          console.error('Server Error:', error.response.data);
        } else if (error.request) {
          toast.error('No response received from server');
          console.error('No response Error:', error.request);
        } else {
          toast.error('Error setting up the request');
          console.error('Error:', error.message);
        }
      });
  };

  // Handle updating an employee
  const handleSubmitUpdate = (e) => {
    e.preventDefault();
    const { id, name, age, department } = currentEmployee;

    // Convert age to number
    const ageNumber = parseInt(age, 10);

    // Client-side validation first
    if (isNaN(ageNumber) || ageNumber < 16) {
      toast.error('Employee must be at least 16 years old.');
      return;
    }

    axios.put(`http://localhost:8000/employees/${id}`, {
      name,
      age: ageNumber,
      department
    })
    .then(response => {
      fetchEmployees();
      setIsEditing(false);
      toast.success('Employee updated successfully!');
    })
    .catch(error => {
      if (error.response) {
        const errorMessage = error.response.data.detail || 'Failed to update employee!';
        toast.error(errorMessage);
        console.error('Server Error:', error.response.data);
      } else if (error.request) {
        toast.error('No response received from server');
        console.error('No response Error:', error.request);
      } else {
        toast.error('Error setting up the request');
        console.error('Error:', error.message);
      }
    });
  };

  // Handle input changes in the form
  const handleChange = (e) => {
    const { name, value } = e.target;
    setCurrentEmployee(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  // Handle clearing the form fields
  const handleClearForm = () => {
    setCurrentEmployee({
      id: null,
      name: '',
      age: '',
      department: ''
    });
    setIsEditing(false);
  };

  return (
    <div className='employee-list-container'>
      <div className='employee-list-header'>
        <h2 className='employee-list-heading'>Employee Management</h2>
        <button
          className='btn-add-employee'
          onClick={() => {
            handleClearForm();
            setIsEditing(true);
          }}
        >
          <PlusCircle size={20} /> Add New Employee
        </button>
      </div>

      {/* Employee List */}
      <div className='employee-cards-container'>
        {employees.length === 0 ? (
          <div className='no-employees'>
            <p>No employees found. Add your first employee!</p>
          </div>
        ) : (
          employees.map(employee => (
            <div key={employee.id} className='employee-card'>
              <div className='employee-card-header'>
                <h3>{employee.name}</h3>
              </div>
              <div className='employee-card-details'>
                <p>
                  <User size={16} />
                  <span><strong>ID: </strong>{employee.id}</span>
                </p>
                <p>
                  <Clock size={16} />
                  <span><strong>Age: </strong>{employee.age}</span>
                </p>
                <p>
                  <Briefcase size={16} />
                  <span><strong>Department: </strong>{employee.department}</span>
                </p>
              </div>
              <div className='employee-card-actions'>
                <button
                  className='btn-delete'
                  onClick={() => handleDelete(employee.id)}
                  title='Delete Employee'
                >
                  <Trash2 size={16} />
                </button>
                <button
                  className='btn-update'
                  onClick={() => handleUpdate(employee)}
                  title='Edit Employee'
                >
                  <Edit2 size={16} />
                </button>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Edit or Add Form Modal */}
      {isEditing && (
        <div className='modal'>
          <div className='modal-content'>
            <h3>{currentEmployee.id ? 'Edit Employee' : 'Add Employee'}</h3>
            <form onSubmit={currentEmployee.id ? handleSubmitUpdate : handleSubmitAdd}>
              <div className='form-group'>
                <label htmlFor='name'>Name</label>
                <input
                  type='text'
                  id='name'
                  name='name'
                  value={currentEmployee.name}
                  onChange={handleChange}
                  required
                  placeholder='Enter employee name'
                />
              </div>
              <div className='form-group'>
                <label htmlFor='age'>Age</label>
                <input
                  type='number'
                  id='age'
                  name='age'
                  value={currentEmployee.age}
                  onChange={handleChange}
                  required
                  min='16'
                  placeholder='Enter employee age'
                />
              </div>
              <div className='form-group'>
                <label htmlFor='department'>Department</label>
                <input
                  type='text'
                  id='department'
                  name='department'
                  value={currentEmployee.department}
                  onChange={handleChange}
                  required
                  placeholder='Enter employee department'
                />
              </div>
              <div className='modal-buttons'>
                <button type='submit' className='btn-save'>
                  {currentEmployee.id ? 'Update' : 'Add'} Employee
                </button>
                <button
                  type='button'
                  className='btn-cancel'
                  onClick={() => setIsEditing(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Toast Notifications */}
      <ToastContainer position='top-right' autoClose={5000} hideProgressBar={false} />
    </div>
  );
};

export default EmployeeList;
