// src/components/EmployeeForm.js
import React, { useState, useEffect } from "react";
import axios from "axios";

const EmployeeForm = ({ employeeId, onSave }) => {
    const [employee, setEmployee] = useState({
        employee_id: "",  // Custom employee_id
        name: "",
        age: 0,
        position: "",  // Ensure this matches the backend field name
    });

    const [errorMessage, setErrorMessage] = useState("");

    useEffect(() => {
        if (employeeId) {
            // Fetch existing employee if updating
            axios.get(`http://localhost:8000/employees/${employeeId}`)
                .then((response) => setEmployee(response.data))
                .catch((error) => console.error("Error fetching employee:", error));
        }
    }, [employeeId]);

    const handleChange = (e) => {
        setEmployee({ ...employee, [e.target.name]: e.target.value });
    };

const handleSubmit = (e) => {
    e.preventDefault();

    // If employee_id is not provided, generate it (for new employees)
    if (!employee.employee_id) {
        employee.employee_id = `EMP-${Date.now()}`;
    }

    if (employeeId) {
        // Update existing employee
        axios.put(`http://localhost:8000/employees/${employeeId}`, employee)
            .then((response) => {
                onSave(response.data);  // Pass the updated data to parent
            })
            .catch((error) => {
                console.error("Error updating employee:", error);
                alert("Error updating employee: " + (error.response?.data?.detail || error.message));
            });
    } else {
        // Create new employee
        axios.post("http://localhost:8000/employees", employee)
            .then((response) => {
                onSave(response.data);  // Pass the new data to parent
            })
            .catch((error) => {
                console.error("Error adding employee:", error);
                alert("Error adding employee: " + (error.response?.data?.detail || error.message));
            });
    }
};


    return (
        <form onSubmit={handleSubmit}>
            {errorMessage && <div style={{ color: "red" }}>{errorMessage}</div>}  {/* Error Display */}
            <input
                type="text"
                name="employee_id"
                value={employee.employee_id}
                onChange={handleChange}
                placeholder="Employee ID"
                disabled={employeeId}  // Disable employee_id field for updating
            />
            <input
                type="text"
                name="name"
                value={employee.name}
                onChange={handleChange}
                placeholder="Name"
                required
            />
            <input
                type="number"
                name="age"
                value={employee.age}
                onChange={handleChange}
                placeholder="Age"
                required
            />
            <input
                type="text"
                name="position"
                value={employee.position}
                onChange={handleChange}
                placeholder="Position"
                required
            />
            <button type="submit">{employeeId ? "Update Employee" : "Add Employee"}</button>
        </form>
    );
};

export default EmployeeForm;
