import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './EmployeeList.css';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentEmployee, setCurrentEmployee] = useState({
    id: null,
    name: '',
    age: '',
    department: ''
  });

  // Fetch all employees from the backend
  useEffect(() => {
    fetchEmployees();
  }, []);

  // Fetch employees from backend
  const fetchEmployees = () => {
    axios.get('http://localhost:8000/employees/')
      .then(response => {
        setEmployees(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the employees!', error);
        toast.error('Failed to fetch employees!');
      });
  };

  // Delete an employee
  const handleDelete = (id) => {
    axios.delete(`http://localhost:8000/employees/${id}`)
      .then(() => {
        setEmployees(employees.filter(employee => employee.id !== id));
        toast.success('Employee deleted successfully!');
      })
      .catch(error => {
        console.error('There was an error deleting the employee!', error);
        toast.error('Failed to delete employee!');
      });
  };

  // Handle updating an employee
  const handleUpdate = (employee) => {
    setIsEditing(true);
    setCurrentEmployee({
      id: employee.id,
      name: employee.name,
      age: employee.age,
      department: employee.department
    });
  };

  // Open modal for adding new employee
  const handleOpenAddModal = () => {
    handleClearForm();
    setIsEditing(true);
  };

  // Submit the updated employee info
  const handleSubmitUpdate = (e) => {
    e.preventDefault();
    const { id, name, age, department } = currentEmployee;

    // Convert age to number
    const ageNumber = parseInt(age, 10);

    // Client-side validation first
    if (isNaN(ageNumber) || ageNumber < 16) {
      toast.error('Employee must be at least 16 years old.');
      return;
    }

    // Proceed with updating employee
    axios.put(`http://localhost:8000/employees/${id}`, {
      name,
      age: ageNumber,
      department
    })
    .then(response => {
      // Refresh the employee list
      fetchEmployees();

      // Reset editing state
      setIsEditing(false);

      // Success notification
      toast.success('Employee updated successfully!');
    })
    .catch(error => {
      // Detailed error handling
      if (error.response) {
        const errorMessage = error.response.data.detail || 'Failed to update employee!';
        toast.error(errorMessage);
        console.error('Server Error:', error.response.data);
      } else if (error.request) {
        toast.error('No response received from server');
        console.error('No response Error:', error.request);
      } else {
        toast.error('Error setting up the request');
        console.error('Error:', error.message);
      }
    });
  };

  // Handle adding new employee
  const handleSubmitAdd = (e) => {
    e.preventDefault();
    const { name, age, department } = currentEmployee;

    // Convert age to number
    const ageNumber = parseInt(age, 10);

    // Client-side validation first
    if (isNaN(ageNumber) || ageNumber < 16) {
      toast.error('Employee must be at least 16 years old.');
      return;
    }

    axios.post('http://localhost:8000/employees/', { name, age: ageNumber, department })
      .then(response => {
        fetchEmployees();
        setIsEditing(false);
        toast.success('Employee added successfully!');
      })
      .catch(error => {
        console.error('There was an error adding the employee!', error);

        // Comprehensive error handling
        if (error.response) {
          // The request was made and the server responded with a status code
          // that falls out of the range of 2xx
          if (error.response.status === 422) {
            // Validation error from Pydantic
            const errorDetails = error.response.data;
            const errorMessage = errorDetails.detail[0].msg || 'Validation failed';
            toast.error(errorMessage);
          } else {
            // Other HTTP error responses
            const errorMessage = error.response.data.detail || 'Failed to add employee!';
            toast.error(errorMessage);
          }
          console.error('Server Error:', error.response.data);
        } else if (error.request) {
          // The request was made but no response was received
          toast.error('No response received from server');
          console.error('No response Error:', error.request);
        } else {
          // Something happened in setting up the request that triggered an Error
          toast.error('Error setting up the request');
          console.error('Error:', error.message);
        }
      });
  };

  // Handle input changes in the form
  const handleChange = (e) => {
    const { name, value } = e.target;
    setCurrentEmployee(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  // Handle clearing the form fields
  const handleClearForm = () => {
    setCurrentEmployee({
      id: null,
      name: '',
      age: '',
      department: ''
    });
    setIsEditing(false);
  };

  return (
    <div className='employee-list-container'>
      <h2 className='employee-list-heading'>Employee List</h2>

      {/* Employee List */}
      <div className='employee-cards-container'>
        {employees.map(employee =>
          <div key={employee.id} className='employee-card'>
            <h3>{employee.name}</h3>
            <p><strong>ID: </strong>{employee.id}</p>
            <p><strong>Age: </strong>{employee.age}</p>
            <p><strong>Department: </strong>{employee.department}</p>
            <div className='employee-card-actions'>
              <button
                className='btn-delete'
                onClick={() => handleDelete(employee.id)}
              >
                Delete
              </button>
              <button
                className='btn-update'
                onClick={() => handleUpdate(employee)}
              >
                Update
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Edit or Add Form Modal */}
      {isEditing &&
  <div className='modal'>
    <div className='modal-content'>
      <h3>{currentEmployee.id ? 'Edit Employee' : 'Add Employee'}</h3>
      <form onSubmit={currentEmployee.id ? handleSubmitUpdate : handleSubmitAdd}>
        <div className='form-group'>
          <label>
            Name:
            <input
              type='text'
              name='name'
              value={currentEmployee.name}
              onChange={handleChange}
              required
              placeholder='Enter employee name'
              className='form-input'
            />
          </label>
        </div>
        <div className='form-group'>
          <label>
            Age:
            <input
              type='number'
              name='age'
              value={currentEmployee.age}
              onChange={handleChange}
              required
              min='16'
              placeholder='Enter employee age'
              className='form-input'
            />
          </label>
        </div>
        <div className='form-group'>
          <label>
            Department:
            <input
              type='text'
              name='department'
              value={currentEmployee.department}
              onChange={handleChange}
              required
              placeholder='Enter department'
              className='form-input'
            />
          </label>
        </div>
        <div className='modal-buttons'>
          <button type='submit' className='btn-save'>Save Changes</button>
          <button type='button' className='btn-clear' onClick={handleClearForm}>Clear</button>
          <button type='button' className='btn-cancel' onClick={() => setIsEditing(false)}>Cancel</button>
        </div>
      </form>
    </div>
  </div>
}

      {/* Toast Notifications */}
      <ToastContainer position='top-right' autoClose={5000} hideProgressBar={false} />
    </div>
  );
};

export default EmployeeList;
