import React, { useState, useEffect } from "react";
import axios from "axios";

const EmployeeList = ({ onUpdate }) => {
    const [employees, setEmployees] = useState([]);

    useEffect(() => {
        // Fetch all employees only on the first render
        axios.get("http://localhost:8000/employees/")
            .then((response) => setEmployees(response.data))
            .catch((error) => console.error("Error fetching employees:", error));
    }, []); // Empty dependency array ensures this effect only runs once

    const handleDelete = (id) => {
        // Delete employee
        axios.delete(`http://localhost:8000/employees/${id}`)
            .then(() => {
                // Using the previous state to ensure you're working with the most up-to-date state
                setEmployees((prevEmployees) =>
                    prevEmployees.filter((employee) => employee.employee_id !== id)
                );
            })
            .catch((error) => console.error("Error deleting employee:", error));
    };

    const handleEdit = (id) => {
        // Trigger the update action in parent
        onUpdate(id);
    };

    return (
        <div>
            <h2>Employee List</h2>
            <ul>
                {employees.map((employee) => (
                    <li key={employee.employee_id}>
                        {employee.name} - {employee.age} - {employee.department}
                        <button onClick={() => handleEdit(employee.employee_id)}>Edit</button>
                        <button onClick={() => handleDelete(employee.employee_id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default EmployeeList;
