import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EmployeeList.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentEmployee, setCurrentEmployee] = useState({
    id: null,
    name: '',
    age: '',
    department: ''
  });

  // Fetch all employees from the backend
  useEffect(() => {
    fetchEmployees();
  }, []);

  // Fetch employees from backend
  const fetchEmployees = () => {
    axios.get('http://localhost:8000/employees/')
      .then(response => {
        setEmployees(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the employees!', error);
        toast.error('Failed to fetch employees!');
      });
  };

  // Validate age function
  const validateAge = (age) => {
    const ageNumber = parseInt(age, 10);

    // Check if age is a number
    if (isNaN(ageNumber)) {
      toast.error('Age must be a valid number.');
      return false;
    }

    // Check if age is at least 16
    if (ageNumber < 16) {
      toast.error('Employee must be at least 16 years old.');
      return false;
    }

    // Check if age is reasonable (less than 100)
    if (ageNumber > 100) {
      toast.error('Please enter a valid age (up to 100 years old).');
      return false;
    }

    return ageNumber;
  };

  // Delete an employee
  const handleDelete = (id) => {
    axios.delete(`http://localhost:8000/employees/${id}`)
      .then(() => {
        setEmployees(employees.filter(employee => employee.id !== id));
        toast.success('Employee deleted successfully!');
      })
      .catch(error => {
        console.error('There was an error deleting the employee!', error);
        toast.error('Failed to delete employee!');
      });
  };

  // Handle updating an employee
  const handleUpdate = (employee) => {
    setIsEditing(true);
    setCurrentEmployee({
      id: employee.id,
      name: employee.name,
      age: employee.age,
      department: employee.department
    });
  };

  // Submit the updated employee info
  const handleSubmitUpdate = (e) => {
    e.preventDefault();
    const { id, name, age, department } = currentEmployee;

    // Validate age
    const validatedAge = validateAge(age);
    if (!validatedAge) return;

    // If age is valid, continue with the update
    axios.put(`http://localhost:8000/employees/${id}`, { name, age: validatedAge, department })
      .then(response => {
        // Refresh the employee list to ensure latest data
        fetchEmployees();
        setIsEditing(false);
        toast.success('Employee updated successfully!');
      })
      .catch(error => {
        console.error('There was an error updating the employee!', error);
        toast.error('Failed to update employee!');
      });
  };

  // Handle adding new employee
  const handleSubmitAdd = (e) => {
    e.preventDefault();
    const { name, age, department } = currentEmployee;

    // Validate age
    const validatedAge = validateAge(age);
    if (!validatedAge) return;

    // If age is valid, proceed to add the new employee
    axios.post('http://localhost:8000/employees/', { name, age: validatedAge, department })
      .then(response => {
        // Refresh the employee list to ensure latest data
        fetchEmployees();
        setIsEditing(false);
        toast.success('Employee added successfully!');

        // Clear the form after successful addition
        handleClearForm();
      })
      .catch(error => {
        console.error('There was an error adding the employee!', error);
        toast.error('Failed to add employee!');
      });
  };

  // Handle input changes in the form
  const handleChange = (e) => {
    const { name, value } = e.target;
    setCurrentEmployee(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  // Handle clearing the form fields
  const handleClearForm = () => {
    setCurrentEmployee({
      id: null,
      name: '',
      age: '',
      department: ''
    });
    setIsEditing(false);
  };

  return (
    <div className='employee-list-container'>
      <h2 className='employee-list-heading'>Employee List</h2>

      {/* Employee List */}
      <div className='employee-cards-container'>
        {employees.map(employee =>
          <div key={employee.id} className='employee-card'>
            <h3>{employee.name}</h3>
            <p><strong>ID: </strong>{employee.id}</p>
            <p><strong>Age: </strong>{employee.age}</p>
            <p><strong>Department: </strong>{employee.department}</p>
            <div className='employee-card-actions'>
              <button className='btn-delete' onClick={() => handleDelete(employee.id)}>Delete</button>
              <button className='btn-update' onClick={() => handleUpdate(employee)}>Update</button>
            </div>
          </div>
        )}
      </div>

      {/* Edit or Add Form Modal */}
      {isEditing &&
        <div className='modal'>
          <div className='modal-content'>
            <h3>{currentEmployee.id ? 'Edit Employee' : 'Add Employee'}</h3>
            <form onSubmit={currentEmployee.id ? handleSubmitUpdate : handleSubmitAdd}>
              <label>
                Name:
                <input
                  type='text'
                  name='name'
                  value={currentEmployee.name}
                  onChange={handleChange}
                  required
                />
              </label>
              <label>
                Age:
                <input
                  type='number'
                  name='age'
                  value={currentEmployee.age}
                  onChange={handleChange}
                  required
                  min='16'
                />
              </label>
              <label>
                Department:
                <input
                  type='text'
                  name='department'
                  value={currentEmployee.department}
                  onChange={handleChange}
                  required
                />
              </label>
              <div className='modal-buttons'>
                <button type='submit'>Save Changes</button>
                <button type='button' onClick={handleClearForm}>Clear</button>
                <button type='button' onClick={() => setIsEditing(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      }

      {/* Toast Notifications */}
      <ToastContainer position='top-right' autoClose={5000} hideProgressBar={false} />
    </div>
  );
};

export default EmployeeList;
