import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentEmployee, setCurrentEmployee] = useState({
    id: null,
    name: '',
    age: '',
    department: ''
  });

  // Fetch all employees from the backend
  useEffect(() => {
    axios.get('http://localhost:8000/employees/')
      .then(response => {
        setEmployees(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the employees!', error);
      });
  }, []);

  // Delete an employee
  const handleDelete = (id) => {
    axios.delete(`http://localhost:8000/employees/${id}`)
      .then(() => {
        setEmployees(employees.filter(employee => employee.id !== id)); // Remove the deleted employee from the state
        toast.success('Employee deleted successfully!');
      })
      .catch(error => {
        console.error('There was an error deleting the employee!', error);
        toast.error('Error deleting employee!');
      });
  };

  // Handle updating an employee
  const handleUpdate = (employee) => {
    setIsEditing(true);
    setCurrentEmployee({
      id: employee.id,
      name: employee.name,
      age: employee.age,
      department: employee.department
    });
  };

  // Submit the updated employee info
  const handleSubmitUpdate = (e) => {
    e.preventDefault();
    const { id, name, age, department } = currentEmployee;

    // Age validation: Show a toast notification if the age is less than 15
    if (age < 15) {
      toast.error('Employee is too young for this job (age must be at least 15)');
      return;
    }

    axios.put(`http://localhost:8000/employees/${id}`, { name, age, department })
      .then(response => {
        setEmployees(employees.map(employee =>
          employee.id === id ? response.data : employee
        ));
        setIsEditing(false); // Close the edit form
        toast.success('Employee updated successfully!');
      })
      .catch(error => {
        console.error('There was an error updating the employee!', error);
        toast.error('Error updating employee!');
      });
  };

  // Submit the new employee info
  const handleSubmitAdd = (e) => {
    e.preventDefault();
    const { name, age, department } = currentEmployee;

    // Age validation
    if (age < 15) {
      toast.error('Employee is too young for this job (age must be at least 15)');
      return;
    }

    axios.post('http://localhost:8000/employees/', { name, age, department })
      .then(response => {
        setEmployees([...employees, response.data]);
        toast.success('Employee added successfully!');
      })
      .catch(error => {
        console.error('There was an error adding the employee!', error);
        toast.error('Error adding employee!');
      });
  };

  // Handle input changes in the form
  const handleChange = (e) => {
    const { name, value } = e.target;
    setCurrentEmployee(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  return (
    <div className="employee-list-container">
      <h2 className="employee-list-heading">Employee List</h2>

      <div className="employee-cards-container">
        {employees.map(employee => (
          <div key={employee.id} className="employee-card">
            <h3>{employee.name}</h3>
            <p><strong>ID:</strong> {employee.id}</p>
            <p><strong>Age:</strong> {employee.age}</p>
            <p><strong>Department:</strong> {employee.department}</p>
            <div className="employee-card-actions">
              <button className="btn-delete" onClick={() => handleDelete(employee.id)}>Delete</button>
              <button className="btn-update" onClick={() => handleUpdate(employee)}>Update</button>
            </div>
          </div>
        ))}
      </div>

      {/* Modal for Adding or Updating Employee */}
      {isEditing && (
        <div className="modal">
          <div className="modal-content">
            <h3>Edit Employee</h3>
            <form onSubmit={handleSubmitUpdate}>
              <label>Name:</label>
              <input
                type="text"
                name="name"
                value={currentEmployee.name}
                onChange={handleChange}
                required
              />
              <label>Age:</label>
              <input
                type="number"
                name="age"
                value={currentEmployee.age}
                onChange={handleChange}
                required
              />
              <label>Department:</label>
              <input
                type="text"
                name="department"
                value={currentEmployee.department}
                onChange={handleChange}
                required
              />
              <div className="modal-buttons">
                <button type="submit">Save Changes</button>
                <button type="button" onClick={() => setIsEditing(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Add New Employee Form */}
      <div className="add-employee-form">
        <h3>Add New Employee</h3>
        <form onSubmit={handleSubmitAdd}>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={currentEmployee.name}
            onChange={handleChange}
            required
          />
          <label>Age:</label>
          <input
            type="number"
            name="age"
            value={currentEmployee.age}
            onChange={handleChange}
            required
          />
          <label>Department:</label>
          <input
            type="text"
            name="department"
            value={currentEmployee.department}
            onChange={handleChange}
            required
          />
          <button type="submit">Add Employee</button>
        </form>
      </div>
    </div>
  );
};

export default EmployeeList;
